// Example of using EF Core in C#\nusing Microsoft.EntityFrameworkCore;\n\npublic class Person\n{\n    public int Id { get; set; }\n    public string FirstName { get; set; }\n    public string LastName { get; set; }\n    public string FullName => $"{FirstName} {LastName}";\n}\n\npublic class AppDbContext : DbContext\n{\n    public DbSet<Person> People { get; set; }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        optionsBuilder.UseMySql("server=localhost;database=testdb;user=root;password=yourpassword", ServerVersion.AutoDetect("server=localhost;database=testdb;user=root;password=yourpassword"));\n    }\n}