using System;\nusing System.Data;\nusing System.Data.Odbc;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Your connection string here";\n        string clobData = new string('A', 5000); // Example large CLOB data\n\n        using (OdbcConnection odbcConnection = new OdbcConnection(connectionString))\n        {\n            odbcConnection.Open();\n\n            string sql = "INSERT INTO YourTable (Id, YourClobColumn) VALUES (?, ?)";\n            using (OdbcCommand cmd = new OdbcCommand(sql, odbcConnection))\n            {\n                // Setting up parameters\n                cmd.Parameters.Add(new OdbcParameter("p1", 1)); // Assuming you have an Id column\n                var clobParam = new OdbcParameter("clobData", OdbcType.Clob);\n                clobParam.Value = clobData;\n                cmd.Parameters.Add(clobParam);\n\n                cmd.ExecuteNonQuery();\n            }\n        }\n    }\n}