protected void UploadButton_Click(object sender, EventArgs e)\n{\n    if (FileUpload1.HasFile)\n    {\n        string filePath = Server.MapPath("~/TempFiles/" + Path.GetFileName(FileUpload1.FileName));\n        FileUpload1.SaveAs(filePath);\n        Session["UploadedFilePath"] = filePath; // Store the path in session for later use\n    }\n}\n\nprotected void SaveButton_Click(object sender, EventArgs e)\n{\n    if (Session["UploadedFilePath"] != null)\n    {\n        string filePath = Session["UploadedFilePath"].ToString();\n        \n        // Read the file and store it wherever you need (e.g., database, external storage)\n        byte[] fileBytes = File.ReadAllBytes(filePath);\n\n        // Store other form data and fileBytes in your database here\n\n        // Clean up the temporary file\n        File.Delete(filePath);\n        Session.Remove("UploadedFilePath");\n    }\n}