using AngleSharp;\nusing System.Threading.Tasks;\n\nasync Task Main()\n{\n    var config = Configuration.Default.WithDefaultLoader();\n    IBrowsingContext context = BrowsingContext.New(config);\n    IDocument document = await context.OpenAsync(req => req.Content("<select id=\"foo_Bar\" name=\"foo.Bar\"><option selected=\"selected\" value=\"1\">One</option><option value=\"2\">Two</option></select>"));\n\n    Console.WriteLine(document.Body.OuterHtml);\n}