public void RemoveUsersFromRoles(string[] usernames, string[] rolenames)\n{\n    using (var dataContext = new YourDataContext())\n    {\n        // Get UserIDs from usernames\n        var userIdsToRemove = dataContext.Users\n                                         .Where(u => usernames.Contains(u.Username))\n                                         .Select(u => u.RecordID)\n                                         .ToList();\n        \n        // Get RoleIDs from rolenames\n        var roleIdsToRemove = dataContext.Roles\n                                          .Where(r => rolenames.Contains(r.Rolename))\n                                          .Select(r => r.RecordID)\n                                          .ToList();\n\n        // Remove the entries in UsersInRole that match the user and role IDs to be removed\n        var usersInRolesToDelete = dataContext.UsersInRoles\n                                              .Where(uir => userIdsToRemove.Contains(uir.UserID) && \n                                                              roleIdsToRemove.Contains(uir.RoleID))\n                                              .ToList();\n\n        dataContext.UsersInRoles.DeleteAllOnSubmit(usersInRolesToDelete);\n        dataContext.SubmitChanges();\n    }\n}