using System.Data.SqlServerCe;\n\nstring connectionString = "Data Source=YourDatabase.sdf;";\nusing (SqlCeConnection connection = new SqlCeConnection(connectionString))\n{\n    connection.Open();\n\n    string insertQuery = "INSERT INTO Deliveries (Date, Number) VALUES (@Date, @Number)";\n    using (SqlCeCommand command = new SqlCeCommand(insertQuery, connection))\n    {\n        // Add parameters to prevent SQL injection\n        command.Parameters.AddWithValue("@Date", DateTime.Now);\n        command.Parameters.AddWithValue("@Number", 12345);\n\n        int rowsAffected = command.ExecuteNonQuery();\n        if (rowsAffected > 0)\n        {\n            Console.WriteLine("Record added successfully.");\n        }\n        else\n        {\n            Console.WriteLine("Failed to add record.");\n        }\n    }\n}