using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string xhtmlContent = @"\n<!DOCTYPE html PUBLIC '-//W3C//DTD XHTML 1.0 Strict//EN'\n    'http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd'>\n<html xmlns='http://www.w3.org/1999/xhtml'>\n  <head>\n    <title>\n    </title>\n  </head>\n  <body contenteditable='true'>\n    <p>\n      Example paragraph content\n    </p>\n    <p>\n      &nbsp;\n    </p>\n    <p>\n      <br />\n      &nbsp;\n    </p>\n    <h1>Header 1</h1>\n  </body>\n</html>";\n\n        string pattern = @"<body[^>]*>(.*?)</body>";\n        Match match = Regex.Match(xhtmlContent, pattern, RegexOptions.Singleline);\n\n        if (match.Success)\n        {\n            string bodyContent = match.Groups[1].Value;\n            Console.WriteLine("Body Content:\n" + bodyContent);\n        }\n        else\n        {\n            Console.WriteLine("No content found inside the body tags.");\n        }\n    }\n}