using System;\nusing System.Diagnostics;\nusing System.Threading;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string processName = "notepad"; // Replace with your application's process name without .exe\n\n        using (PerformanceCounter cpuCounter = new PerformanceCounter("Process", "% Processor Time", processName))\n        {\n            cpuCounter.NextValue(); // Initial call to discard first value\n            Thread.Sleep(1000); // Wait for a second before getting the next value\n            float usage = cpuCounter.NextValue();\n\n            Console.WriteLine($"CPU Usage of {processName}: {usage / Environment.ProcessorCount}%");\n        }\n    }\n}