// Example C# code using UIAutomation to find a control and click it\n   using System.Windows.Automation;\n   \n   AutomationElement window = AutomationElement.RootElement.FindFirst(TreeScope.Children, new PropertyCondition(AutomationElement.NameProperty, "Title of Application Window"));\n   AutomationElement button = window.FindFirst(TreeScope.Descendants, new PropertyCondition(AutomationElement.ControlTypeProperty, ControlType.Button));\n   InvokePattern pattern = (InvokePattern)button.GetCurrentPattern(InvokePattern.Pattern);\n   pattern.Invoke();