using System;\nusing System.Diagnostics;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string noisyOutput = "This is a test message with a bell character \a";\n        SafeTrace.WriteLine(noisyOutput);\n    }\n\n    public static class SafeTrace\n    {\n        private const char BellCharacter = (char)7; // ASCII code for the bell character\n\n        public static void WriteLine(string value)\n        {\n            string sanitizedValue = value.Replace(BellCharacter, ' ');\n            Trace.WriteLine(sanitizedValue);\n        }\n    }\n}