using System;\n\npublic class AlphabetIterator\n{\n    public static string IncrementString(string input)\n    {\n        char[] chars = input.ToCharArray();\n        bool carry = true;\n        \n        for (int i = chars.Length - 1; i >= 0 && carry; i--)\n        {\n            if (chars[i] < 'z')\n            {\n                chars[i]++;\n                carry = false;\n            }\n            else\n            {\n                chars[i] = 'a';\n            }\n        }\n\n        if (carry)\n        {\n            // If we have a carry after the first character, we need to add an 'a' at the beginning.\n            char[] newChars = new char[chars.Length + 1];\n            newChars[0] = 'a';\n            Array.Copy(chars, 0, newChars, 1, chars.Length);\n            return new string(newChars);\n        }\n\n        return new string(chars);\n    }\n\n    public static void Main()\n    {\n        Console.WriteLine(IncrementString("a"));   // Outputs: b\n        Console.WriteLine(IncrementString("z"));   // Outputs: aa\n        Console.WriteLine(IncrementString("az"));  // Outputs: ba\n        Console.WriteLine(IncrementString("bnc")); // Outputs: bnd\n        Console.WriteLine(IncrementString("zzz")); // Outputs: aaaa\n    }\n}