using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<int> list = new List<int> { 1, 2, 3, 4, 5 };\n        int startIndex = 1;\n        int endIndex = 4;\n\n        // Using LINQ to create a sublist\n        IList<int> subList = list.Skip(startIndex).Take(endIndex - startIndex).ToList();\n\n        Console.WriteLine(string.Join(", ", subList));\n    }\n}