using System;\nusing System.Collections.ObjectModel;\n\npublic class CollectionExample\n{\n    public static void Main()\n    {\n        ObservableCollection<string> observableList = new ObservableCollection<string>();\n\n        // Subscribe to the CollectionChanged event.\n        observableList.CollectionChanged += (s, e) =>\n        {\n            switch (e.Action)\n            {\n                case System.Collections.Specialized.NotifyCollectionChangedAction.Add:\n                    Console.WriteLine("Added: " + string.Join(", ", e.NewItems));\n                    break;\n                case System.Collections.Specialized.NotifyCollectionChangedAction.Remove:\n                    Console.WriteLine("Removed: " + string.Join(", ", e.OldItems));\n                    break;\n            }\n        };\n\n        // Add items to the collection.\n        observableList.Add("Item1");\n        observableList.Add("Item2");\n\n        // Remove an item from the list.\n        observableList.RemoveAt(0);\n    }\n}