using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        DataSet ds = new DataSet();\n        DataTable table = new DataTable();\n        ds.Tables.Add(table);\n\n        // Adding initial columns\n        table.Columns.Add("column_1", typeof(string));\n        table.Columns.Add("column_2", typeof(int));\n        table.Columns.Add("column_4", typeof(string));\n\n        // Column to insert\n        DataColumn column3 = new DataColumn("column_3", typeof(string));\n\n        // Get the index of "column_2"\n        int insertIndex = table.Columns["column_2"].Ordinal + 1;\n\n        // Insert "column_3" after "column_2"\n        table.Columns.Add(column3);\n        for (int i = table.Columns.Count - 1; i > insertIndex; i--)\n        {\n            DataColumn column = table.Columns[i];\n            table.Columns.Remove(column);\n            table.Columns.Insert(insertIndex, column);\n        }\n\n        // Display columns to verify order\n        foreach (DataColumn col in table.Columns)\n        {\n            Console.WriteLine(col.ColumnName);\n        }\n    }\n}