using System;\nusing System.Management;\n\npublic class RemoteProcessKiller\n{\n    public static void KillRemoteProcess(string machineName, string processName)\n    {\n        try\n        {\n            // Connect to the WMI service on the remote machine.\n            ManagementScope scope = new ManagementScope($"\\\\{machineName}\\root\\cimv2");\n            scope.Connect();\n\n            // Create object searcher for processes matching the name.\n            ObjectQuery query = new ObjectQuery($"SELECT * FROM Win32_Process WHERE Name = '{processName}'");\n            using (ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query))\n            {\n                foreach (ManagementObject process in searcher.Get())\n                {\n                    uint id;\n                    // Attempt to terminate the process by id.\n                    process.InvokeMethod("Terminate", null);\n                    process.GetObjectData().Properties["ProcessId"].Value.TryCastTo(out id);\n\n                    Console.WriteLine($"Killed '{processName}' with PID: {id}");\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to kill the process on {machineName}: {ex.Message}");\n        }\n    }\n\n    static void Main(string[] args)\n    {\n        KillRemoteProcess("YourRemoteMachineName", "ProcessName.exe");\n    }\n}