using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class WebPageReader\n{\n    public async Task<string> GetWebPageSourceAsync(string url)\n    {\n        using (HttpClient client = new HttpClient())\n        {\n            try\n            {\n                HttpResponseMessage response = await client.GetAsync(url);\n                response.EnsureSuccessStatusCode();\n                string responseBody = await response.Content.ReadAsStringAsync();\n                return responseBody;\n            }\n            catch (HttpRequestException e)\n            {\n                Console.WriteLine($"Request error: {e.Message}");\n                return null;\n            }\n        }\n    }\n}\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        WebPageReader reader = new WebPageReader();\n        string url = "https://www.example.com"; // Replace with your target URL\n        string sourceCode = await reader.GetWebPageSourceAsync(url);\n        \n        if (sourceCode != null)\n        {\n            Console.WriteLine(sourceCode);\n        }\n    }\n}