public bool SendMail(MailMessage message)\n{\n    message.From = new MailAddress(AppProperties.FromMailAddress, AppProperties.FromDisplayName);\n    SmtpClient smtp = new SmtpClient("localhost")\n    {\n        Port = 25,\n        EnableSsl = false, // SSL typically not supported on local SMTP\n        UseDefaultCredentials = true // or set credentials if needed\n        // Credentials = new NetworkCredential(username, password) // only if required by SMTP server\n    };\n    try\n    {\n        smtp.Send(message);\n        return true;\n    }\n    catch (Exception ex)\n    {\n        // Log exception here for debugging\n        Console.WriteLine(ex.Message); \n        return false;\n    }\n}