using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        // Replace [Server-Name] with your server name and [IIS-Site-ID] with your IIS site ID.\n        string serverName = "[Server-Name]";\n        string iisSiteId = "[IIS-Site-ID]";\n\n        using (var requestsTotalCounter = new PerformanceCounter("ASP.NET Apps v2.0.50727", "Requests Total", "_LM_W3SVC_" + iisSiteId + "_ROOT", serverName))\n        {\n            try\n            {\n                int currentRequestsTotal = (int)requestsTotalCounter.NextValue();\n                Console.WriteLine($"Initial requests total: {currentRequestsTotal}");\n\n                // Wait for 1 second to get the difference in requests.\n                System.Threading.Thread.Sleep(1000);\n\n                int newRequestsTotal = (int)requestsTotalCounter.NextValue();\n                int requestDifference = newRequestsTotal - currentRequestsTotal;\n\n                Console.WriteLine($"New requests in last second: {requestDifference}");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}