[HttpPost]\npublic ActionResult Create([Bind(Exclude="Id")]Contact contact)\n{\n    try\n    {\n        // Validate ModelState automatically\n        if (ModelState.IsValid)\n        {\n            contact.Save();\n            return RedirectToAction("Index");\n        }\n    }\n    catch (Exception ex)\n    {\n        // Handle exceptions and add model state errors if needed\n        ModelState.AddModelError(string.Empty, ex.Message);\n    }\n\n    // Return the view with error messages if validation fails\n    return View(contact);\n}