using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\n\npublic class HtmlSender\n{\n    public void SendHtmlToWebsite(string url, string htmlContent)\n    {\n        try\n        {\n            // Create a request using a URL that can receive a post.\n            HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n            request.Method = "POST";\n\n            // Set the ContentType property of the WebRequest.\n            request.ContentType = "application/x-www-form-urlencoded";\n\n            // Convert the HTML content to a byte array\n            byte[] byteArray = Encoding.UTF8.GetBytes("htmlContent=" + Uri.EscapeDataString(htmlContent));\n\n            // Set the ContentLength property of the WebRequest.\n            request.ContentLength = byteArray.Length;\n\n            // Get the request stream.\n            using (Stream dataStream = request.GetRequestStream())\n            {\n                // Write the byte array to the DataStream of the request\n                dataStream.Write(byteArray, 0, byteArray.Length);\n            }\n\n            // Get the response from the server.\n            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n            {\n                Console.WriteLine("Response status code: " + response.StatusCode);\n\n                // Optionally read the response if needed\n                using (Stream dataStreamResponse = response.GetResponseStream())\n                {\n                    StreamReader reader = new StreamReader(dataStreamResponse ?? throw new InvalidOperationException());\n                    string responseFromServer = reader.ReadToEnd();\n\n                    // Write the response to console.\n                    Console.WriteLine("Response from Server: " + responseFromServer);\n                }\n            }\n        }\n        catch (WebException ex)\n        {\n            // Handle exceptions\n            WebResponse errorResponse = ex.Response;\n            using (Stream responseStream = errorResponse?.GetResponseStream())\n            {\n                if (responseStream != null)\n                {\n                    StreamReader reader = new StreamReader(responseStream, Encoding.GetEncoding("utf-8"));\n                    string errorText = reader.ReadToEnd();\n                    Console.WriteLine(errorText);\n                }\n            }\n        }\n    }\n}