// Example of using a simple Standard SQL query in C#\nusing (var connection = new SqlConnection(connectionString))\n{\n    var command = new SqlCommand("SELECT * FROM YourTable WHERE Column1=@value1 AND Column2=@value2", connection);\n    command.Parameters.AddWithValue("@value1", someValue1);\n    command.Parameters.AddWithValue("@value2", someValue2);\n\n    connection.Open();\n    using (var reader = command.ExecuteReader())\n    {\n        while (reader.Read())\n        {\n            // Process data\n        }\n    }\n}