static string GetColumnName(int index)\n{\n    if (index < 1) return string.Empty; // Handle invalid input\n    const int alphabetsCount = 26;\n    string columnName = "";\n\n    while (index > 0)\n    {\n        int remainder = (index - 1) % alphabetsCount;\n        columnName = ((char)((remainder) + 'A')) + columnName;\n        index = (index - 1) / alphabetsCount;\n    }\n\n    return columnName;\n}