using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        string query = "SELECT * FROM Win32_DeviceChangeEvent WHERE EventType=2";\n        ManagementEventWatcher watcher = new ManagementEventWatcher(query);\n        watcher.EventArrived += DeviceInserted;\n        watcher.Start();\n\n        Console.WriteLine("Monitoring USB devices. Press any key to stop...");\n        Console.ReadKey();\n        \n        watcher.Stop();\n    }\n\n    private static void DeviceInserted(object sender, EventArrivedEventArgs e)\n    {\n        Console.WriteLine("USB device inserted.");\n        // Here you can add more code to get detailed information\n    }\n}