try\n{\n    if (!EventLog.SourceExists(APPLICATIONNAME))\n    {\n        // This should not happen since source creation is typically done outside of the app.\n        EventLog.CreateEventSource(APPLICATIONNAME, EVENTLOGNAME);\n    }\n    \n    EventLog.WriteEntry(APPLICATIONNAME, "Service started", EventLogEntryType.Information);\n}\ncatch (SecurityException ex)\n{\n    // Log to a different location or handle this exception as needed\n    Console.WriteLine("Security Exception: " + ex.Message);\n}\ncatch (EventLogException ex)\n{\n    // Handle logging errors specifically here\n    Console.WriteLine("EventLog Exception: " + ex.Message);\n}