using System;\nusing System.Net;\nusing System.Net.Sockets;\nusing System.Text;\n\nclass BasicSmtpServer {\n    private TcpListener listener;\n    \n    public void Start(int port) {\n        listener = new TcpListener(IPAddress.Any, port);\n        listener.Start();\n        Console.WriteLine("SMTP Server started on port {0}", port);\n\n        while (true) {\n            TcpClient client = listener.AcceptTcpClient();\n            _ = HandleClientAsync(client); // Asynchronous handling\n        }\n    }\n\n    private async Task HandleClientAsync(TcpClient client) {\n        using (client)\n        using (NetworkStream stream = client.GetStream()) {\n            byte[] buffer = new byte[1024];\n            int bytesRead;\n            \n            while ((bytesRead = await stream.ReadAsync(buffer, 0, buffer.Length)) != 0) {\n                string dataReceived = Encoding.ASCII.GetString(buffer, 0, bytesRead);\n                Console.WriteLine("Client > " + dataReceived);\n\n                // Simple response for demonstration\n                byte[] response = Encoding.ASCII.GetBytes("250 OK\r\n");\n                await stream.WriteAsync(response, 0, response.Length);\n            }\n        }\n    }\n\n    public void Stop() {\n        listener.Stop();\n    }\n\n    static async Task Main(string[] args) {\n        BasicSmtpServer server = new BasicSmtpServer();\n        server.Start(2525); // Non-standard port for development\n    }\n}