using System;\nusing System.Linq;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        XNamespace nsXHtml = "http://www.w3.org/1999/xhtml";\n\n        XElement xhtml = new XElement(nsXHtml + "html",\n            new XElement(nsXHtml + "head",\n                new XElement(nsXHtml + "title", "Test Page")\n            ),\n            new XElement(nsXHtml + "body",\n                new XElement(nsXHtml + "p", "Hello, world!")\n            )\n        );\n\n        // Querying the XML\n        var titleElement = from el in xhtml.Descendants(nsXHtml + "title")\n                           select el.Value;\n\n        foreach (var title in titleElement)\n        {\n            Console.WriteLine(title);\n        }\n    }\n}