// Assuming `officesWithDoctors` is your original data source\nvar aggregatedOffices = officesWithDoctors\n    .GroupBy(office => office.OfficeName)\n    .Select(group => new \n    {\n        OfficeName = group.Key,\n        Doctors = group.Select(doctor => doctor.DoctorName).ToList()\n    })\n    .ToList();\n\n// Bind the aggregated data to your repeater\nrepeaterControl.DataSource = aggregatedOffices;\nrepeaterControl.DataBind();