using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Data.SqlTypes;\nusing Microsoft.SqlServer.Server;\nusing Oracle.ManagedDataAccess.Client;\n\npublic partial class StoredProcedures\n{\n    [SqlProcedure]\n    public static void FetchFromOracleRefCursor(SqlString oracleConnectionString)\n    {\n        using (var conn = new OracleConnection(oracleConnectionString.Value))\n        {\n            conn.Open();\n            var cmd = new OracleCommand("BEGIN :refcur := pkg_name.procedure_name(:param); END;", conn);\n            cmd.Parameters.Add(new OracleParameter("refcur", OracleDbType.RefCursor)).Direction = ParameterDirection.Output;\n            cmd.Parameters.Add("param", OracleDbType.Int32).Value = 1; // Adjust parameter type and value\n            \n            using (var reader = cmd.ExecuteReader())\n            {\n                SqlDataRecord record = null;\n\n                while (reader.Read())\n                {\n                    if (record == null)\n                    {\n                        var metadata = new[]\n                        {\n                            new SqlMetaData("column1", SqlDbType.NVarChar, 50),\n                            // Define other columns as necessary\n                        };\n                        record = new SqlDataRecord(metadata);\n                    }\n\n                    record.SetString(0, reader.GetString(0)); // Set values for the output record\n                    SqlCommandPipe.SendResultsRow(record); // Send each row back to the caller\n                }\n            }\n        }\n    }\n};