protected void Page_Load(object sender, EventArgs e)\n{\n    string requestedDomain = Request.Url.Host;\n\n    switch (requestedDomain.ToLower())\n    {\n        case "mydomain.com":\n            Response.Redirect("https://www.yoursiteforfirstdomain.com");\n            break;\n        case "myotherdomain.com":\n            Response.Redirect("https://www.yoursiteforseconddomain.com");\n            break;\n        default:\n            Response.StatusCode = 404; // Return a 404 if the domain is not recognized\n            Response.End();\n            break;\n    }\n}