using System;\nusing System.Collections.Generic;\n\npublic class Program {\n    public static void Main() {\n        IEnumerable<string> days = new List<string>{ "monday", "tuesday", "wednesday", "wednesday" };\n        \n        // Use a HashSet to store unique values\n        HashSet<string> distinctDaysSet = new HashSet<string>();\n        \n        foreach (string day in days) {\n            distinctDaysSet.Add(day);\n        }\n        \n        // Convert the HashSet back to IEnumerable\n        IEnumerable<string> distinctDays = distinctDaysSet;\n        \n        // Print out the distinct days\n        foreach (string day in distinctDays) {\n            Console.WriteLine(day);\n        }\n    }\n}