using System;\nusing System.IO.Pipes;\n\nclass Program {\n    static void Main() {\n        const string requestPipeName = "irb_request.pipe";\n        const string responsePipeName = "irb_response.pipe";\n\n        using (NamedPipeClientStream pipeToServer = new NamedPipeClientStream(".", requestPipeName, PipeDirection.Out))\n        using (NamedPipeClientStream pipeFromServer = new NamedPipeClientStream(".", responsePipeName, PipeDirection.In)) {\n            pipeToServer.Connect();\n            pipeFromServer.Connect();\n\n            Console.WriteLine("Enter commands to send to IRB. Type 'exit' to quit.");\n            string input;\n            while ((input = Console.ReadLine()) != "exit") {\n                StreamWriter writer = new StreamWriter(pipeToServer) { AutoFlush = true };\n                writer.WriteLine(input);\n\n                StreamReader reader = new StreamReader(pipeFromServer);\n                string response = reader.ReadLine();\n                Console.WriteLine("IRB Response: " + response);\n            }\n        }\n    }\n}