using System.Drawing; // for Size and Point structures\nusing System.Windows.Forms; // for PictureBox control\n\n// Assuming SceneSize is the size of your main scene\nSize SceneSize = new Size(800, 600);\n\n// Assuming PreviewSize is the current size of your preview window\nSize PreviewSize = new Size(80, 60);\n\n// Calculate scaling factors\nfloat scaleX = (float)PreviewSize.Width / SceneSize.Width;\nfloat scaleY = (float)PreviewSize.Height / SceneSize.Height;\n\nforeach (SceneObject obj in Scene)\n{\n    PictureBox p = new PictureBox();\n\n    // Scale the size of the object for the preview\n    p.Size = new Size((int)(obj.size.Width * scaleX), (int)(obj.size.Height * scaleY));\n\n    // Scale and adjust the location of the object for the preview\n    p.Location = new Point((int)(obj.location.X * scaleX), (int)(obj.location.Y * scaleY));\n\n    // Add the PictureBox to your preview window's controls\n    previewWindow.Controls.Add(p);\n}