using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    // Declare the COM interface you want to use.\n    [Guid("02DFE5FB-6CAA-478B-BD93-E4CD3EFCF841")]\n    [InterfaceType(ComInterfaceType.InterfaceIsIUnknown)]\n    [ComImport]\n    interface IMyComInterface\n    {\n        void MethodOne();\n        void MethodTwo([MarshalAs(UnmanagedType.BStr)] string param);\n    }\n\n    class MyClass\n    {\n        static void Main(string[] args)\n        {\n            // Create an instance of the COM object.\n            Guid guid = new Guid("A06438C7-042E-4ABD-A7F6-E59B1A911D9D");\n            IMyComInterface comObject = (IMyComInterface)Activator.CreateInstance(Type.GetTypeFromCLSID(guid));\n\n            // Call methods on the COM object.\n            comObject.MethodOne();\n            comObject.MethodTwo("Hello, COM!");\n        }\n    }\n}