using System.Data.SqlServerCe;\n\nstring connectionString = "Data Source=YourDatabase.sdf;";\nusing (SqlCeConnection connection = new SqlCeConnection(connectionString))\n{\n    connection.Open();\n\n    string updateQuery = "UPDATE Deliveries SET Number = @Number WHERE CustomerID = @CustomerID AND OrderID = @OrderID";\n    using (SqlCeCommand command = new SqlCeCommand(updateQuery, connection))\n    {\n        // Add parameters to prevent SQL injection\n        command.Parameters.AddWithValue("@Number", 67890);\n        command.Parameters.AddWithValue("@CustomerID", 10);\n        command.Parameters.AddWithValue("@OrderID", 1234);\n\n        int rowsAffected = command.ExecuteNonQuery();\n        if (rowsAffected > 0)\n        {\n            Console.WriteLine("Record updated successfully.");\n        }\n        else\n        {\n            Console.WriteLine("Failed to update record. It may not exist.");\n        }\n    }\n}