using System;\nusing System.Globalization;\n\npublic class TimeConverter\n{\n    public static string ConvertUtcToLocalDisplayTime(DateTime utcDateTime)\n    {\n        // Get the local time zone information\n        TimeZoneInfo localZone = TimeZoneInfo.Local;\n\n        // Convert UTC time to local time\n        DateTime localDateTime = TimeZoneInfo.ConvertTimeFromUtc(utcDateTime, localZone);\n\n        // Format and return the local date-time as a string suitable for display\n        return localDateTime.ToString("yyyy-MM-dd HH:mm:ss", CultureInfo.InvariantCulture);\n    }\n}\n\n// Example usage in an ASP.NET page or Web API controller:\nDateTime utcTime = DateTime.UtcNow;\nstring localTimeString = TimeConverter.ConvertUtcToLocalDisplayTime(utcTime);\n\n// Output to your view (this is a simplified example)\nResponse.Write($"Local time: {localTimeString}");