[HttpGet]\n   public JsonResult GetPartNumbers(string query)\n   {\n       using (var db = new YourDbContext())\n       {\n           var partNumbers = db.PartNumbers\n               .Where(p => p.Number.Contains(query) || p.Description.Contains(query))\n               .Select(p => new { p.Number, p.Description })\n               .Take(10) // Limit the number of results for performance and usability\n               .ToList();\n\n           return Json(partNumbers, JsonRequestBehavior.AllowGet);\n       }\n   }