// Assuming ARUser is an Active Record class and you are creating a new user\nvar newUser = new ARUser { Name = "John Doe" };\n\n// Assuming MyEntity is another Active Record class associated with ARUser\nvar entity = new MyEntity { User = newUser, Insert = true };\n\n// When you save the entity, since Insert = true, both the entity and the newUser will be inserted into the database\nusing (new SessionScope())\n{\n    entity.Save();\n}