using (OracleConnection conn = new OracleConnection(connStr))\nusing (OracleTransaction trans = conn.BeginTransaction())\nusing (OracleCommand command = new OracleCommand(cmdTxt, conn, trans))\n{\n    try\n    {\n        command.ExecuteNonQuery();\n        trans.Commit(); // Commit the transaction if no exception occurs.\n    }\n    catch (Exception ex)\n    {\n        trans.Rollback(); // Rollback in case of an exception\n        // Handle or log the exception\n        throw; // Re-throw if needed, after logging/error handling.\n    }\n}