using System;\nusing System.ComponentModel;\nusing System.Windows.Forms;\n\npublic class NewCombo : ComboBox\n{\n    public NewCombo()\n    {\n        // Set your default value here\n        DropDownItems.Clear();\n        for (int i = 0; i < 50; i++)\n        {\n            // Adding items as an example, assuming you populate with items.\n            Items.Add($"Item {i}");\n        }\n    }\n\n    [DefaultValue(50)]\n    public new int DropDownItems\n    {\n        get => base.Items.Count;\n        set\n        {\n            if (value < 0)\n                throw new ArgumentOutOfRangeException(nameof(value), "DropDownItems must be non-negative.");\n            \n            Items.Clear();\n            for (int i = 0; i < value; i++)\n            {\n                // Add your items here, this is just an example.\n                Items.Add($"Item {i}");\n            }\n        }\n    }\n}