using System;\nusing System.Xml.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string vsSettingsPath = Environment.GetFolderPath(Environment.SpecialFolder.MyDocuments) \n            + @"\Visual Studio {version}\Settings\CurrentSettings.vssettings";\n\n        XDocument settingsDoc = XDocument.Load(vsSettingsPath);\n        \n        // Find the SolutionMRU section and remove it\n        var solutionMru = settingsDoc.Descendants("SolutionMRU").FirstOrDefault();\n        if (solutionMru != null)\n        {\n            solutionMru.Remove(); // Clearing all entries in the Solution MRU list.\n            // Optionally, you can recreate an empty <SolutionMRU></SolutionMRU> node here\n            var newSolutionMru = XElement.Parse("<SolutionMRU></SolutionMRU>");\n            settingsDoc.Root.Add(newSolutionMru);\n        }\n\n        // Save changes back to file\n        settingsDoc.Save(vsSettingsPath);\n    }\n}