public partial class Course\n{\n    // Custom property to get the first category for a course\n    public Category FirstCategory\n    {\n        get\n        {\n            using (var context = new YourDataContext())\n            {\n                return context.CourseCategories\n                    .Where(cc => cc.CourseID == this.CourseID)\n                    .Join(context.Categories,\n                          cc => cc.CategoryID, // outerKeySelector\n                          c => c.CategoryID,   // innerKeySelector\n                          (cc, c) => c)        // resultSelector\n                    .FirstOrDefault();\n            }\n        }\n    }\n}