using System;\nusing System.Xml.Linq;\nusing System.Xml.XPath;\n\nnamespace XPathExample\n{\n    class Program\n    {\n        static void Main()\n        {\n            XDocument doc = XDocument.Load("yourfile.xml");\n            \n            // Assuming there is a namespace, otherwise remove this part\n            XmlNamespaceManager nsmgr = new XmlNamespaceManager(new NameTable());\n            nsmgr.AddNamespace("ns", "http://www.yournamespace.com");\n\n            // Using the root of the document as the context node\n            XElement node = doc.Root;\n            var carElement = node.XPathSelectElement("./ns:car", nsmgr);\n            \n            if (carElement != null)\n            {\n                XAttribute descriptionAttr = carElement.Attribute("description");\n                string description = descriptionAttr?.Value ?? "No description found";\n                Console.WriteLine(description);\n            }\n            else\n            {\n                Console.WriteLine("Car element not found.");\n            }\n        }\n    }\n}