public void DirSearch(string sDir)\n{\n    try\n    {\n        foreach (string d in Directory.GetDirectories(sDir))\n        {\n            DirectoryInfo dInfo = new DirectoryInfo(d);\n            FileAttributes attributes = dInfo.Attributes;\n\n            // Check if the attribute contains ReparsePoint flag using bitwise operations\n            if ((attributes & FileAttributes.ReparsePoint) != FileAttributes.ReparsePoint)\n            {\n                foreach (string f in Directory.GetFiles(d, "*.*")) // Use a wildcard or specific pattern as needed\n                {\n                    ListViewItem lvi = new ListViewItem();\n                    lvi.Text = f;\n                    lvi.ImageIndex = 1;\n                    lvi.Tag = "tag";\n                    \n                    var subItem = new ListViewItem.ListViewSubItem { Text = "sub bugger" };\n                    lvi.SubItems.Add(subItem);\n\n                    subItem = new ListViewItem.ListViewSubItem { Text = d };\n                    lvi.SubItems.Add(subItem);\n\n                    listView1.Items.Add(lvi);\n                }\n                DirSearch(d);\n            }\n        }\n    }\n    catch (UnauthorizedAccessException)\n    {\n        // Handle access denied gracefully\n        Console.WriteLine("Access to the path '{0}' is denied.", sDir);\n    }\n    catch (Exception ex)\n    {\n        // Handle other exceptions if needed\n        Console.WriteLine("An error occurred: {0}", ex.Message);\n    }\n}