using System;\nusing System.Data.SqlTypes;\n\npublic class DateTimeStorageExample {\n    public static void Main() {\n        DateTime dt = new DateTime(2023, 1, 1, 0, 0, 0);\n        SqlDateTime sqlDt = new SqlDateTime(dt);\n\n        // Extracting binary representation\n        byte[] binaryData = sqlDt.Data;\n\n        Console.WriteLine("Binary Representation:");\n        for (int i = 0; i <binaryData.Length; i++) {\n            Console.Write(binaryData[i].ToString("X2") + " ");\n        }\n    }\n}