var refSize = Referrals.Count;\nif (refSize == 0)\n{\n    var newReferral = new Referral(referringURL.Trim(), referringWords.Trim(), 3);\n    Referrals.Add(newReferral);\n}\nelse\n{\n    bool found = false;\n\n    for (int i = 0; i < refSize && !found; i++)\n    {\n        if (Referrals[i].URL == referringURL.Trim())\n        {\n            // URL is present, check Keyword\n            if (Referrals[i].Keywords.Contains(referringWords))\n            {\n                Referrals[i].Occurrences++;\n            }\n            else\n            {\n                // If the keyword isn't there, add to keywords list\n                Referrals[i].Keywords.Add(referringWords);\n                Referrals[i].Occurrences++;  // Assuming you want to count this as a new occurrence too\n            }\n\n            found = true;\n        }\n    }\n\n    if (!found)\n    {\n        // URL and Keyword not found in existing referrals, add as new\n        var newReferral = new Referral(referringURL.Trim(), referringWords.Trim(), 3);\n        Referrals.Add(newReferral);\n    }\n}