public static async Task<T> CallServiceAsync<T>(Func<Task<T>> serviceCall)\n    {\n        using (var channel = CreateChannel())\n        {\n            try\n            {\n                return await serviceCall();\n            }\n            catch (TimeoutException ex)\n            {\n                // Handle timeout exception\n                throw;\n            }\n            finally\n            {\n                try\n                {\n                    channel.CloseAsync().Wait();\n                }\n                catch (CommunicationException ex)\n                {\n                    // Handle communication exception during close\n                }\n                catch (TimeoutException ex)\n                {\n                    // Handle timeout exception during close\n                }\n                catch (Exception ex)\n                {\n                    channel.Abort();\n                }\n            }\n        }\n    }\n\n    private static MyServiceClient CreateChannel()\n    {\n        return new MyServiceClient("YourEndpointName");\n    }