using System;\nusing System.Collections.Generic;\nusing System.ComponentModel;\nusing System.Net;\nusing System.Windows;\nusing System.Windows.Controls;\nusing System.Windows.Data;\nusing System.Linq;\n\npublic partial class MainPage : UserControl\n{\n    public ObservableCollection<object> Data { get; set; }\n\n    public MainPage()\n    {\n        InitializeComponent();\n        Data = new ObservableCollection<object>();\n        LoadData();\n    }\n\n    private void LoadData()\n    {\n        WebClient client = new WebClient();\n        Uri serviceUri = new Uri("http://yourserver/service.svc/GetData", UriKind.Absolute);\n        client.DownloadStringCompleted += (s, e) =>\n            {\n                if (!e.Cancelled && e.Error == null)\n                {\n                    // Parse JSON here and add to Data collection\n                    var dynamicData = Newtonsoft.Json.JsonConvert.DeserializeObject<List<Dictionary<string, object>>>(e.Result);\n\n                    foreach (var item in dynamicData)\n                    {\n                        Data.Add(item);\n                    }\n\n                    dataGrid.ItemsSource = Data;\n                }\n            };\n        client.DownloadStringAsync(serviceUri);\n    }\n}