using System;\nusing System.Windows.Forms;\nusing System.Linq;\n\npublic partial class YourForm : Form\n{\n    private YourDbContext dbContext = new YourDbContext();\n    private BindingSource bindingSource = new BindingSource();\n\n    public YourForm()\n    {\n        InitializeComponent();\n\n        LoadData();\n    }\n\n    private void LoadData()\n    {\n        // Retrieve data from the database using LINQ to Entities\n        var items = dbContext.YourEntities.ToList();\n\n        // Bind the list of entities to a binding source\n        bindingSource.DataSource = items;\n        \n        // Set the DataSource of the DataGridView to the binding source\n        dataGridView1.DataSource = bindingSource;\n    }\n\n    private void btnSaveChanges_Click(object sender, EventArgs e)\n    {\n        // Save changes back to the database\n        dbContext.SaveChanges();\n    }\n}