using System;\nusing System.Globalization;\nusing System.Windows.Data;\nusing System.Web;\n\npublic class HtmlToPlainTextConverter : IValueConverter\n{\n    public object Convert(object value, Type targetType, object parameter, CultureInfo culture)\n    {\n        if (value == null)\n            return string.Empty;\n\n        // Decode HTML entities\n        string htmlEncoded = value.ToString();\n        string decodedString = HttpUtility.HtmlDecode(htmlEncoded);\n\n        return decodedString;\n    }\n\n    public object ConvertBack(object value, Type targetType, object parameter, CultureInfo culture)\n    {\n        throw new NotImplementedException("ConvertBack is not required for this converter.");\n    }\n}