var query = from a in DbContext.Appointments.AsNoTracking()\n            where a.RowStatus == 1 && a.Type == 1\n            from b in DbContext.AppointmentFormulas\n                .Where(bf => bf.AppointmentId == a.AppointmentId && bf.RowStatus == 1)\n                .DefaultIfEmpty() // This mimics LEFT JOIN for AppointmentFormula\n            join d in DbContext.Types on a.Type equals d.TypeId\n            from e in DbContext.AppointmentForms\n                .Where(ea => ea.AppointmentId == a.AppointmentId)\n                .DefaultIfEmpty() // This mimics LEFT JOIN for AppointmentForm\n            select new \n            {\n                a.AppointmentId,\n                a.Status,\n                a.Type,\n                a.Title,\n                Days = b.Days, // Accessing Days safely since it's a LEFT JOIN result\n                d.Description,\n                FormId = e.FormId // Accessing FormId safely since it's a LEFT JOIN result\n            };\n\nvar orderedQuery = query.OrderBy(q => q.Type);